import styled from "styled-components";
import { useState, useEffect } from "react";
import tombstone from "../assets/tombstone.png";

const Confirmation = ({ reservationId }) => {
    const [confirmData, setConfirmData] = useState(null)
    useEffect(() => {
        fetch(`/api/get-reservation/${reservationId}`)
            .then(res => res.json())    
            .then((data) => {
                setConfirmData(data.data)


            }, [reservationId])
    })
    return (
        <>
        {!state ? (<h1>Loading...</h1>) : 
        (
            <Wrapper>
            <Container>
            <Title> Your flight is confirmed!</Title>
            <Info>
            <Text><Tag>Reservation #:</Tag> {state.id}</Text>
            <Text><Tag>Flight #:</Tag> {state.flight}</Text>
            <Text><Tag>Seat #:</Tag> {state.seat}</Text>
            <Text><Tag>Name: </Tag>{state.firstName} {state.lastName}</Text>
            <Text><Tag>Email:</Tag> {state.email}</Text>
            </Info>
            </Container>
               {/* // TODO: Display the POSTed reservation information */}
            <Img src={tombstone} alt="tombstone RIP" />
        </Wrapper>


        )}

        </>


    )
};

const Wrapper = styled.div`
height: 100vh;
width: 100vw;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
`
const Container = styled.div`
margin-top: 125px;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
align-content: center;
border:solid 2px var(--color-alabama-crimson);
border-radius: 4px;
width: 500px;
`
const Info = styled.div` 
margin-bottom: 15px;
`
const Text = styled.p`
line-height: 40px;
color: black;
font-size: 18px;
`
const Tag = styled.span`
font-weight: bolder;
`
const Title = styled.h1`
color: var(--color-alabama-crimson);
border-bottom: var(--color-alabama-crimson) solid 2px;
margin: 0px 15px;
padding-bottom: 20px;
`
const Img = styled.img`
max-height: 180px;
margin-top: 30px;
margin-bottom: 30px;
`

export default Confirmation;
